package codeup;
import java.util.Scanner;
public class codeup71 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner sc = new Scanner(System.in);
		
		
		while(true) {
		int input = sc.nextInt();
			if (input != 0)
				System.out.println(input);
			else
				break;
		}
		
		sc.close();

	}

}
