package codeup;
import java.util.Scanner;
public class codeup89 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner sc = new Scanner(System.in);
		int a = sc.nextInt();
		int d = sc.nextInt();
		int n = sc.nextInt();
		int result = a + d*(n-1);
		
		System.out.println(result);
		
		sc.close();

	}

}
